@import "tailwindcss";

/* Global light-only color scheme */
:root {
  color-scheme: light; /* force light UI even if OS is dark */

  --background: #f8fafc; /* slate-50-ish */
  --foreground: #0f172a; /* slate-900-ish */
}

/* Tailwind v4 inline theme tokens */
@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --font-sans: var(--font-geist-sans);
  --font-mono: var(--font-geist-mono);
}

/* Hard lock light background + dark text */
html,
body {
  background-color: var(--background);
  color: var(--foreground);
}

/* Senior-friendly base scaling: bump rem size globally */
html {
  font-size: 17px;
}

@media (min-width: 1024px) {
  html {
    font-size: 18px;
  }
}

/* Base typography */
@layer base {
  body {
    @apply bg-slate-50 text-slate-900;
    font-family: var(
      --font-geist-sans,
      system-ui,
      -apple-system,
      BlinkMacSystemFont,
      "Segoe UI",
      sans-serif
    );
    line-height: 1.7;
  }

  h1 {
    @apply text-3xl md:text-4xl font-semibold tracking-tight text-slate-900;
  }

  h2 {
    @apply text-2xl md:text-3xl font-semibold tracking-tight text-slate-900;
  }

  h3 {
    @apply text-lg md:text-xl font-semibold text-slate-900;
  }

  /* Default paragraph: comfortable for older eyes */
  p {
    @apply text-base md:text-lg leading-relaxed text-slate-700;
  }
}

/* Shared button styles (used in hero + pilot ribbon + pilot card) */
@layer components {
  .btn-primary {
    @apply inline-flex items-center justify-center rounded-md bg-amber-600 px-4 py-2
      text-sm font-semibold text-white shadow-sm
      hover:bg-amber-500
      focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-amber-600
      focus-visible:ring-offset-2 focus-visible:ring-offset-slate-50;
  }

  .btn-secondary {
    @apply inline-flex items-center justify-center rounded-md border border-slate-300
      bg-white px-4 py-2 text-sm font-semibold text-slate-800
      hover:border-slate-400 hover:bg-slate-50
      focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-sky-600
      focus-visible:ring-offset-2 focus-visible:ring-offset-slate-50;
  }

  .btn-compact {
    @apply inline-flex items-center justify-center rounded-md bg-amber-600 px-3 py-1.5
      text-xs font-semibold text-white shadow-sm
      hover:bg-amber-500
      focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-amber-600
      focus-visible:ring-offset-2 focus-visible:ring-offset-amber-50;
  }
}

